import React, { useState } from 'react';

function App() {
  // State variables to hold the values of email and password inputs
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  // Function to handle changes in the email input
  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  // Function to handle changes in the password input
  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  // Function to handle form submission
  const handleSubmit = (event) => {
    event.preventDefault();
    // You can perform any actions here, such as validation or submitting the form
    console.log('Email:', email);
    console.log('Password:', password);
  };

  return (
    <div className="bg-white">
      <div className="flex gap-5 max-md:flex-col max-md:gap-0">
        {/* Rest of your JSX */}
        <div className="flex flex-col max-md:max-w-full">
          <div className="flex gap-1.5 self-start text-2xl leading-7 whitespace-nowrap">
            <div className="grow text-black">Email</div>
            <div className="text-orange-500">*</div>
          </div>
          <input
            className="justify-center px-4 py-4 mt-3.5 text-xl leading-7 text-black bg-white border-solid border-[3px] border-stone-300 max-md:max-w-full"
            type="text"
            value={email} // Bind value to state variable
            onChange={handleEmailChange} // Handle input changes
          />
        </div>
        {/* Rest of your JSX */}
        <div className="flex flex-col max-md:max-w-full">
          <div className="flex gap-1.5 self-start text-2xl leading-7">
            <div className="grow text-black">Password</div>
            <div className="text-orange-500">*</div>
          </div>
          <input
            className="justify-center px-4 py-4 mt-3.5 text-xl leading-7 text-black bg-white border-solid border-[3px] border-stone-300 max-md:max-w-full"
            type="password"
            value={password} // Bind value to state variable
            onChange={handlePasswordChange} // Handle input changes
          />
        </div>
        {/* Rest of your JSX */}
        <div className="flex flex-col justify-center mt-6 max-w-full text-xl tracking-normal leading-6 text-center text-black whitespace-nowrap w-[182px]">
          <div
            className="justify-center px-7 py-3.5 bg-orange-500 max-md:px-5"
            onClick={handleSubmit} // Handle form submission
            style={{ cursor: 'pointer' }}
          >
            Login
          </div>
        </div>
        {/* Rest of your JSX */}
      </div>
    </div>
  );
}

export default App;
