import React, { useState } from "react";
import loginimg from '../images/loginUI.png';
import alertimg from '../images/alert.svg';

function LoginPage() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleUsernameChange = (event) => {
        setUsername(event.target.value);
    };

    const handlePasswordChange = (event) => {
        setPassword(event.target.value);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await fetch('http:/f.com/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ username, password }),
            });
            if (!response.ok) {
                throw new Error('Failed to login');
            }

            console.log('login successful');
        } catch (error) {
            setError('Failed to login, please try again.');
        }
    };

    return (
        <div className="bg-white flex flex-col max-w-screen-xl mx-auto px-4 py-8 md:flex-row md:gap-5">
            <div className="w-full md:w-3/5 bg-orange-500 flex justify-center items-center">
                <img src={loginimg} alt="Login" />
            </div>
            <div className="w-full md:w-2/5">
                <div className="px-4 py-8">
                    <h2 className="text-4xl font-bold text-orange-500 mb-4">Welcome to the Admin Page</h2>
                    <p className="text-xl text-gray-600 mb-8">
                        In publishing and graphic design, Lorem ipsum is a placeholder text commonly used to demonstrate the visual form
                    </p>
                    <form onSubmit={handleSubmit} className="space-y-6">
                        <div className="flex flex-col">
                            <label htmlFor="username" className="flex items-center text-2xl text-gray-700">
                                Email <span className="text-orange-500">*</span>
                            </label>
                            <input
                                id="username"
                                type="text"
                                value={username}
                                onChange={handleUsernameChange}
                                className="input-field"
                                required
                            />
                        </div>
                        <div className="flex flex-col">
                            <label htmlFor="password" className="flex items-center text-2xl text-gray-700">
                                Password <span className="text-orange-500">*</span>
                            </label>
                            <input
                                id="password"
                                type="password"
                                value={password}
                                onChange={handlePasswordChange}
                                className="input-field"
                                required
                            />
                        </div>
                        {error && (
                            <div className="flex items-center space-x-2">
                                <img src={alertimg} className="w-8 h-8" alt="Error Icon" />
                                <span className="text-red-500">{error}</span>
                            </div>
                        )}
                        <button type="submit" className="btn-primary w-full mt-4">Login</button>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default LoginPage;
